+        return [
+            self::ACTIVE,
+            self::INACTIVE,
+            self::SUSPENDED,
+            self::DELETED
+        ];
+    }
+
+    public static function isValid(string $status): bool
+    {
+        return in_array($status, self::getValidStatuses());
+    }
+}
