+/**
+ * User entity class
+ */
+class User
+{
+    private int $id;
+    private string $name;
+    private string $email;
+    private string $status;
+    private DateTime $createdAt;
+    private ?DateTime $lastLoginAt = null;
+    private array $permissions;
+    private array $metadata;
+
+    public function __construct(
+        int $id = 0,
+        string $name = '',
+        string $email = '',
+        string $status = UserStatus::ACTIVE,
+        array $permissions = [],
+        array $metadata = []
+    ) {
+        $this->id = $id;
+        $this->name = $name;
+        $this->email = $email;
+        $this->status = $status;
+        $this->createdAt = new DateTime();
+        $this->permissions = $permissions;
+        $this->metadata = $metadata;
+    }
+
+    // Getters
+    public function getId(): int
+    {
+        return $this->id;
+    }
+
+    public function getName(): string
+    {
+        return $this->name;
+    }
+
+    public function getEmail(): string
+    {
+        return $this->email;
+    }
+
+    public function getStatus(): string
+    {
+        return $this->status;
+    }
+
+    public function getCreatedAt(): DateTime
+    {
+        return $this->createdAt;
+    }
+
+    public function getLastLoginAt(): ?DateTime
+    {
+        return $this->lastLoginAt;
+    }
+
+    public function getPermissions(): array
+    {
+        return $this->permissions;
+    }
+
+    public function getMetadata(): array
+    {
+        return $this->metadata;
+    }
+
+    // Setters
+    public function setId(int $id): void
+    {
+        $this->id = $id;
+    }
+
+    public function setName(string $name): void
+    {
+        $this->name = $name;
+    }
+
+    public function setEmail(string $email): void
+    {
+        $this->email = $email;
+    }
+
+    public function setStatus(string $status): void
+    {
+        if (!UserStatus::isValid($status)) {
+            throw new InvalidArgumentException("Invalid status: {$status}");
+        }
+        $this->status = $status;
+    }
+
+    public function setPermissions(array $permissions): void
+    {
+        $this->permissions = $permissions;
+    }
+
+    public function setMetadata(array $metadata): void
+    {
+        $this->metadata = $metadata;
+    }
+
+    // Business methods
+    public function activate(): void
+    {
+        $this->status = UserStatus::ACTIVE;
+    }
+
+    public function deactivate(): void
+    {
+        $this->status = UserStatus::INACTIVE;
+    }
+
+    public function suspend(): void
+    {
+        $this->status = UserStatus::SUSPENDED;
+    }
+
+    public function hasPermission(string $permission): bool
+    {
+        return in_array($permission, $this->permissions) || 
+               in_array(Permission::ADMIN, $this->permissions);
+    }
+
+    public function addPermission(string $permission): void
+    {
+        if (!$this->hasPermission($permission)) {
+            $this->permissions[] = $permission;
+        }
+    }
+
+    public function removePermission(string $permission): void
+    {
+        $this->permissions = array_values(array_filter(
+            $this->permissions,
+            fn($p) => $p !== $permission
+        ));
+    }
+
+    public function updateLastLogin(): void
+    {
+        $this->lastLoginAt = new DateTime();
+    }
+
+    public function getDisplayName(): string
+    {
+        return "{$this->name} ({$this->email})";
+    }
+
+    public function isActive(): bool
+    {
+        return $this->status === UserStatus::ACTIVE;
+    }
+
+    public function toArray(): array
+    {
+        return [
+            'id' => $this->id,
+            'name' => $this->name,
+            'email' => $this->email,
+            'status' => $this->status,
+            'created_at' => $this->createdAt->format('Y-m-d H:i:s'),
+            'last_login_at' => $this->lastLoginAt?->format('Y-m-d H:i:s'),
+            'permissions' => $this->permissions,
+            'metadata' => $this->metadata
+        ];
+    }
+
+    public static function fromArray(array $data): self
+    {
+        $user = new self(
+            $data['id'] ?? 0,
+            $data['name'] ?? '',
+            $data['email'] ?? '',
+            $data['status'] ?? UserStatus::ACTIVE,
+            $data['permissions'] ?? [],
+            $data['metadata'] ?? []
+        );
+
+        if (isset($data['created_at'])) {
+            $user->createdAt = new DateTime($data['created_at']);
+        }
+
+        if (isset($data['last_login_at'])) {
+            $user->lastLoginAt = new DateTime($data['last_login_at']);
+        }
+
+        return $user;
+    }
+
+    public function __toString(): string
+    {
+        return "User[{$this->id}]: {$this->getDisplayName()}";
+    }
+}
