private function parseCsv(string $data): array
{
    $lines = explode("\n", trim($data));
    if (empty($lines)) {
        return [];
    }

    $headers = str_getcsv(array_shift($lines));
    $result = [];

    foreach ($lines as $line) {
        if (trim($line) !== '') {
            $values = str_getcsv($line);
            $result[] = array_combine($headers, $values);
        }
    }

    return $result;
}

