+
+    private function mapToUser(array $data): User
+    {
+        $userData = [
+            'id' => (int) $data['id'],
+            'name' => $data['name'],
+            'email' => $data['email'],
+            'status' => $data['status'],
+            'created_at' => $data['created_at'],
+            'last_login_at' => $data['last_login_at'],
+            'permissions' => json_decode($data['permissions'], true) ?? [],
+            'metadata' => json_decode($data['metadata'], true) ?? []
